#include <string>
#include <vector>
#include <iostream>
#include <thread>
#include <atomic>
#include <chrono>

#define W  2

std::atomic<int> max_val(0);
std::atomic<int> max_i(0);
std::atomic<int> max_j(0);

int s(char a, char b){
    if (a == b){
        return 3;
    }
    return -3;
}

int max_neigh(int up, int diag, int left){
    int max_val = std::max(up, std::max(diag, left));

    if (up == max_val){
        return 0;
    }
    else if (diag == max_val){
        return 1;
    }
    return 2;
}

void smithWatermanThread(std::string a, std::string b, int start, int end, int len_a, std::vector<std::vector<int> >& H){
    int score1, score2, score3;
    for (int i = start; i < end; i++){
        for (int j = 1; j < len_a + 1; j++){
            while (H[i-1][j-1]<0 || H[i-1][j]<0 || H[i][j-1]<0){

            }
            score1 = H[i-1][j-1] + s(a[j-1], b[i-1]);
            score2 = H[i-1][j] - W;
            score3 = H[i][j-1] - W;

            H[i][j] = std::max(0, std::max(score1, std::max(score2, score3)));
            if (H[i][j]> max_val){
                max_val = H[i][j];
                max_i = i;
                max_j = j;
                } 

        }
    
    }
}


void smithWaterman(std::string a, std::string b, int num_threads){

    int len_a = a.length();
    int len_b = b.length();
    // std::cout<<len_a<<std::endl;
    // std::cout<<len_b<<std::endl;

    std::vector<std::thread> workers(num_threads);

    int chunk = len_b / num_threads;


    std::vector<std::vector<int> > H(len_b + 1, std::vector<int>(len_a+1, -1));
    for (int i = 0; i< len_a + 1; i++){
        H[0][i] = 0;
    }
    for (int i = 0; i< len_b + 1; i++){
        H[i][0] = 0;
    }


    int score1, score2, score3;


    for (int i = 0; i < num_threads - 1; i++){
        // std::cout<<i<<std::endl;
        workers[i] = std::thread(&smithWatermanThread, a, b, i*chunk + 1, ((i+1) * chunk) + 1, len_a, std::ref(H));
        
    }
    
    smithWatermanThread(a, b, (num_threads-1)*chunk + 1, len_b+1, len_a, std::ref(H));

    for (int i = 0; i <num_threads - 1; i++){
        workers[i].join();
    }

    


    int up, diag, left;

    std::string aligned_a, aligned_b;

    while (max_i > 0 && max_j > 0 && H[max_i][max_j]!= 0){
        diag = H[max_i-1][max_j-1] + s(a[max_j-1], b[max_i-1]);
        up = H[max_i-1][max_j] - W;

        if (H[max_i][max_j] == diag){
            max_i--;
            max_j--;
            aligned_a = a[max_j] + aligned_a;
            aligned_b = b[max_i] + aligned_b;

        }
        else if(H[max_i][max_j] == up){
            max_i--;
            aligned_a = '-' + aligned_a;
            aligned_b = b[max_i] + aligned_b;

        }
        else{
            max_j--;
            aligned_a = a[max_j] + aligned_a;
            aligned_b = '-' + aligned_b;

        }
    }
    // std::cout<<a<<std::endl;
    // std::cout<<b<<std::endl;
    // std::cout<<aligned_a<<std::endl;
    // std::cout<<aligned_b<<std::endl;

}




int main(){
    auto start = std::chrono::high_resolution_clock::now();
    std::string a = "";
    std::string b = "";
    smithWaterman(a,b,5);
    auto elapsed = std::chrono::high_resolution_clock::now();
    std::chrono::duration<double> duration = elapsed- start;
    std::cout << "Execution time: " << duration.count() << " seconds." << std::endl;
    return 0;
}